import React from "react";
import Slider from "react-slick";

class ImageSlider extends React.Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2000, // set the time between each slide
    };

    return (
      <Slider {...settings}>
        <div>
          <img
            src="https://res.cloudinary.com/dlmgs0z2s/image/upload/v1681136413/slider-photo-1_hbwhrj.jpg"
            alt=" "
          />
        </div>
        <div>
          <img
            src="https://res.cloudinary.com/dlmgs0z2s/image/upload/v1681137074/slider-photo-2_zizmy2.jpg"
            alt=" "
          />
        </div>
        <div>
          <img
            src="https://res.cloudinary.com/dlmgs0z2s/image/upload/v1681138564/slider-photo-3_huuszj.jpg"
            alt=" "
          />
        </div>
      </Slider>
    );
  }
}

export default ImageSlider;
