import React from "react";
import Card from "../Card/Card";
import "./featuredProducts.scss";
import useFetch from "../../hooks/useFetch";

const FeaturedProducts = ({ type }) => {
  const { data, loading, error } = useFetch(
    `/products?populate=*&[filters][type][$eq]=${type}`
  );

  function reveal() {
    var reveals = document.querySelectorAll(".reveal");
    for (var i = 0; i < reveals.length; i++) {
      var windowHeight = window.innerHeight;
      var elementTop = reveals[i].getBoundingClientRect().top;
      var elementVisible = 150;

      if (elementTop < windowHeight - elementVisible) {
        reveals[i].classList.add("active");
      } else {
        reveals[i].classList.remove("active");
      }
    }
  }

  window.addEventListener("scroll", reveal);

  return (
    <div className="featuredProducts">
      <div className="top">
        <h1>{type} products</h1>
        <p>
          Welcome to the new world order. Gone are the days of cut-and-dried
          trend forecasting; the bell curve no longer rings true. Before the
          internet became indelible in our lives, forecasting agencies would
          seek out whispers in counterculture, then sell the trends to
          corporations that would shower them upon the masses. We tuned into
          MTVâ€™s TRL or flipped through the magazines in the checkout line to see
          what the latest thing was: like layered babydoll dresses that everyone
          paired with combat boots in the â€˜90s or the oversized clothing that
          hung off the frames of nearly every rapper in the early 2000s. The
          20-year cycle is no longer king either. Social media democratized, and
          scrambled, all of it.
        </p>
      </div>
      <div className="bottom">
        {error ? (
          <span className="errMessage">Something went wrong!</span>
        ) : loading ? (
          <div class="lds-ring">
            <div></div>
            <div></div>
            <div></div>
            <div></div>
          </div>
        ) : (
          data?.map((item) => (
            <div className="card-container">
              <Card item={item} key={item.id} />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default FeaturedProducts;
